import { clsx, type ClassValue } from "clsx";
import { format } from "date-fns";
import { numberFormatRules } from "./regex.lib";
import { twMerge } from "./twMerge.lib";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

export const recordToArray = <T>(record: Record<string, T>) =>
  Object.entries(record).map(([value, label]) => ({ label, value }));

export const arrayToRecord = <T>(array: Array<T>, key: keyof T) =>
  array.reduce(
    (acc, item) => {
      const recordKey = item[key];
      if (typeof recordKey === "string" || typeof recordKey === "number") {
        acc[recordKey as string] = item;
      }
      return acc;
    },
    {} as Record<string, T>,
  );

export const extractNumbers = (input?: string) => {
  return input?.replace(/\D/g, ""); // Removes all non-digits
};

export const getFilenameFromUrl = (url?: string) => {
  return url?.split("/").pop();
};

export const formatDateForPayload = (date?: string) =>
  date ? format(date, "yyyy-MM-dd") : "";

export const numberFormat = (value: string) => {
  return value.replace(/[^0-9]/g, "").replace(numberFormatRules, ".");
};

export const numberFormatReverse = (value: string) => {
  return parseFloat(value.replace(/\./g, "") ?? "0");
};

export const ifEmpty = <T>(value: T, defaultValue: T) => {
  if (
    value === undefined ||
    value === null ||
    value === false ||
    (typeof value === "string" && value.trim() === "")
  ) {
    return defaultValue;
  }
  return value;
};

import { extendTailwindMerge } from "tailwind-merge";

export const twMerge = extendTailwindMerge({
  extend: {
    classGroups: {
      flex: ["flex-1", "flex-0"],
      "font-size": [
        "text-h1",
        "text-h2",
        "text-h3",
        "text-h4",
        "text-h5",
        "text-h6",
        "text-lg",
        "text-md",
        "text-sm",
        "text-xl",
        "text-xs",
      ],
      "shadow-color": ["text-shadow-*"],
    },
  },
});

import { PhoneNumberUtil } from "google-libphonenumber";

export const passwordRules =
  /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*/\\'`;=+\-_~(),.?":{}|<>])[^\s]{8,}$/;

export const emailRules =
  /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z0-9\\-]+\.)+[a-zA-Z]{2,}))$/;

export const phoneNumberRules =
  /^\s*(?:\+?(\d{1,3}))?[-. (]*(\d{3})[-. )]*(\d{3})[-. ]*(\d{5})(?: *x(\d+))?\s*$/;

export const numberRules = /^-?\d+(\.\d+)?$/;

export const numberFormatRules = /\B(?=(\d{3})+(?!\d))/g;

export const websiteRules =
  /^(https?:\/\/)?(www\.)?[a-zA-Z0-9-]+(\.[a-zA-Z]{2,})\/?.*$/;
export const linkedinRules =
  /^(https?:\/\/)?([a-zA-Z0-9-]+\.)?linkedin\.com\/[a-zA-Z0-9@:%._\+~#=\/-]+\/?$/;
export const igRules =
  /^(https?:\/\/)?(www\.)?instagram\.com\/[a-zA-Z0-9@:%._\+~#=\/-]+\/?$/;
export const xRules =
  /^(https?:\/\/)?(www\.)?x\.com\/[a-zA-Z0-9@:%._\+~#=\/-]+\/?$/;
export const fbRules =
  /^(https?:\/\/)?(www\.)?facebook\.com\/[a-zA-Z0-9@:%._\+~#=\/-]+\/?$/;

export const chamberOfCommerceRules =
  /\b(HRA|HRB)\s?\d{1,6}\b|\b(ATU)\s?\d{1,9}\b|\b(CHE)\s?\d{1,9}\b$/;

export const emptyWyswyg = /^(<p><\/p>\n|<p>&nbsp;<\/p>\n)$/;

export const acceptPdf = "application/pdf";
export const acceptImage = "image/png, image/jpeg, image/webp";

const phoneUtil = PhoneNumberUtil.getInstance();
export const phoneNumberValidate = (phone: string) => {
  try {
    return phoneUtil.isValidNumber(phoneUtil.parseAndKeepRawInput(phone));
  } catch {
    return false;
  }
};
